document.domain = 'infosoftware.pl'

var sitemapObj = document.getElementById("inst344");
if(sitemapObj){
	var siteContent = document.getElementsByClassName("sitemap-content")[0]
	if(siteContent){
		siteContent.appendChild(sitemapObj);
	}
}	

var newsletter = document.getElementsByClassName("newsletterEditor")[0];
if(newsletter){
	var iframe = document.createElement('iframe');
	iframe.src = 'https://islearning.infosoftware.pl/newsletter';
	iframe.style.border = 'none';
	iframe.style.width = '100%';
	iframe.style.minHeight = '100vh';
	newsletter.appendChild(iframe);
}

if(document.getElementById("carousel-pauseBtn")){
	document.getElementById("carousel-pauseBtn").addEventListener("click", function () {
		$('#mooveslideshow').carousel('pause')
		document.getElementById("carousel-pauseBtn").style.display = "none";
		document.getElementById("carousel-playBtn").style.display = "block";
	});
}

if(document.getElementById("carousel-playBtn")){
	document.getElementById("carousel-playBtn").addEventListener("click", function () {
		$('#mooveslideshow').carousel('cycle')
		document.getElementById("carousel-playBtn").style.display = "none";
		document.getElementById("carousel-pauseBtn").style.display = "block";
	});
}

